mapper.update:
update BBS.BBS_USER
  set 
    USER_PASSWORD = #{userPassword,jdbcType=VARCHAR}
  where USER_ID = #{userId,jdbcType=DECIMAL}

update BBS.BBS_USER
  set 
    USER_EMAIL = #{userEmail,jdbcType=VARCHAR}
  where USER_ID = #{userId,jdbcType=DECIMAL}

update BBS.BBS_USER
  set USER_PHONE = #{userPhone,jdbcType=VARCHAR}
  where USER_ID = #{userId,jdbcType=DECIMAL}
